mapper.select:
select exists (
	select *
	from tbl_union_answer
	where gameId = #{gameId} and round = #{round} and answer = #{message}
) as isExist;

select exists (
	select *
	from tbl_union_submitted_answer
	where gameId = #{gameId} and round = #{round} and answer = #{message} and score > 0
) as isExist;

select *
from tbl_union_card
where name = (
	select card
	from tbl_union_problem
	where gameId = #{gameId} and round = #{round} and idx = #{idx}
);
